css:
#variables {
	display: none;
}

@media screen {
	.has-variables {
		display: none;
	}
}

.print-format td,
.print-format th {
	vertical-align: middle !important;
}

@media print {
	.has-variables {
		display: block;
	}
	table,
	th,
	td {
		border: 1.5px solid #000;
		border-collapse: collapse;
		font-size: 10px;
	}
}
.watermark{
        background-image: url(/files/output-onlinepngtools.png);
        background-repeat:no-repeat;
        background-position:center;
        background-size:60%;
    }

.left,.right{
    display:inline-block;
}
.left{
    float:left;
}
.right{
    float:right;
}

