css:
@media screen {
    thead,tfoot{
        display:none;
    }
    .page-header, .page-footer {
        display:none;
    }
}
@media print {
    .page-header-space {
        height: 210px;
    }
    .page-footer-space{
        height: 120px;
    }
    .page-header {
        position: fixed;
        top: 0;
        width:100%;
        margin-top:0cm;
    }
    .page-footer {
        bottom:0;
        position:fixed;
        padding-top: 10px;
    }
}
